mapper.update:
update comm_mage
set del_flag = '1',update_by = #{updateBy,jdbcType=VARCHAR},update_date = #{updateDate,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select distinct cam.*
from comm_mage cm
       left join comm_attri_set cas on cm.id = cas.comm_id
       left join comm_attri_mage cam on cas.attri_id = cam.id
where cm.pid = #{id}
   or cm.id = #{id} and cam.effe_inval_state = '0' and cam.del_flag = '0';
