mapper.select:
select cpi.id id, comm_id commId, memb_id membId, comm_title commTitle, application_visit_id applicationVisitId, Recommend recommend, pay_method_type payMethodType, brand_mage_id brandMageId, comm_state_type commStateType, cp.url pictureUrl, avm.unit_num unitNum, (select IFNULL(ROUND(sum(comm_total_count), 0),0) volume from order_info where pay_ord_date is not null ) as volume, maxPrice, minPrice from comm_pub_info cpi left join application_visit_mage avm on cpi.application_visit_id=avm.id left join comm_picture cp on cp.comm_pub_id=cpi.id left join (select max(price) as maxPrice,comm_pub_id from level_spec_quote group by comm_pub_id) lsq on lsq.comm_pub_id=cpi.id left join (select min(price) as minPrice,comm_pub_id from level_spec_quote group by comm_pub_id) lsq1 on lsq1.comm_pub_id=cpi.id where cpi.del_flag = '0' and cp.comm_picture_type='0'
and cpi.comm_title like concat('%',#{standardTypeQuery},'%') or avm.unit_num = #{standardTypeQuery}
and comm_state_type=#{commStateType}
and comm_id=#{commId};

mapper.update:
update comm_pub_info set comm_state_type=#{state}

  and 1=1
  and id in

         #{item}

mapper.select:
select avm.id id, CONCAT(unit_num, ' ', application_name) as name
from application_visit_mage avm
where avm.del_flag = '0';

select * from comm_pub_info

  and 1=1

    and comm_state_type = #{state}

  and id in

    #{item}
