mapper.select:
SELECT oi.id,oi.comm_pub_id,oi.ord_num,oi.buyer_id,oi.refer_ord_date,oi.pay_method_type,oi.comm_total_price,oi.source_type,oi.valid_start_date, oi.valid_end_date,oi.pay_dead_line_date,oi.amount_payable,oi.order_state_type, cpi.comm_title,cpi.application_visit_id, cp.url, cm.comm_catg_name, mi.pet_name,mi.regist_tel FROM order_info oi LEFT JOIN memb_info mi ON mi.id = oi.buyer_id LEFT JOIN comm_pub_info cpi ON cpi.id = oi.comm_pub_id LEFT JOIN comm_mage cm ON cm.id = cpi.comm_id LEFT JOIN comm_picture cp ON cp.comm_pub_id = cpi.id and cp.comm_picture_type = '0' WHERE oi.del_flag = '0'
AND oi.ord_num LIKE CONCAT('%', #{ordNum}, '%')
AND oi.buyer_id LIKE CONCAT('%', #{membId}, '%')
AND oi.order_state_type = #{orderStateType}
AND oi.refer_ord_date >= #{timeStart}
AND oi.refer_ord_date <= #{timeEnd}
AND cm.comm_catg_name like CONCAT('%', #{commCatgName}, '%')
ORDER BY IFNULL(oi.refer_ord_date,oi.create_date) DESC

SELECT oi.order_state_type, count(oi.order_state_type) FROM order_info oi LEFT JOIN memb_info mi ON mi.id = oi.buyer_id LEFT JOIN comm_pub_info cpi ON cpi.id = oi.comm_pub_id LEFT JOIN comm_mage cm ON cm.id = cpi.comm_id WHERE oi.del_flag = '0'
AND oi.ord_num LIKE CONCAT('%', #{ordNum}, '%')
AND oi.buyer_id LIKE CONCAT('%', #{membId}, '%')
AND oi.order_state_type = #{orderStateType}
AND oi.refer_ord_date >= #{timeStart}
AND oi.refer_ord_date <= #{timeEnd}
AND cm.comm_catg_name like CONCAT('%', #{commCatgName}, '%')
GROUP BY oi.order_state_type

select 

from order_info
where id = #{id,jdbcType=BIGINT}

mapper.update:
update order_info
set del_flag = '1',update_by = #{updateBy,jdbcType=VARCHAR},update_date = #{updateDate,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}

mapper.select:
SELECT @rownum := @rownum + 1 AS rankNum, t.* FROM ( SELECT mi.regist_tel registTel, mi.entp_name entpName, COUNT( oi.id ) ordSum, SUM( comm_total_price ) commTotalPrice FROM order_info oi LEFT JOIN memb_info mi ON oi.buyer_id = mi.id WHERE oi.del_flag = '0' AND oi.order_state_type = '4'
AND date_sub( curdate(), INTERVAL 30 DAY ) <= oi.pay_ord_date
AND date_sub( curdate(), INTERVAL 90 DAY ) <= oi.pay_ord_date
AND oi.pay_ord_date >= #{startDate}
AND oi.pay_ord_date <= #{endDate}
GROUP BY oi.buyer_id ORDER BY comm_total_price DESC, ordSum DESC ) t,(SELECT @rownum := 0) r

SELECT oi.ord_num, oi.refer_ord_date, mi.pet_name, oi.amount_payable, oi.pay_method_type, oi.source_type, oi.order_state_type, dict.label as orderStateName FROM order_info oi LEFT JOIN memb_info mi ON mi.id = oi.buyer_id LEFT JOIN (SELECT VALUE,label FROM sys_dict WHERE TYPE = 'order_state_type') dict ON oi.order_state_type = dict.value where oi.del_flag = '0'
and oi.refer_ord_date >= #{startDate,jdbcType=BIGINT}
and oi.refer_ord_date < #{endDate,jdbcType=BIGINT}
and oi.ord_num like concat('%',#{searchKey},'%')
order by oi.refer_ord_date

SELECT order_state_type, COUNT(*) AS effOrdCount, COUNT(DISTINCT(buyer_id)) AS effMembCount, SUM(comm_total_count) as effCommCount, ROUND(SUM(amount_payable),2) AS effOrdAmount FROM order_info WHERE del_flag = '0'
AND date_sub( curdate(), INTERVAL 30 DAY ) <= pay_ord_date
AND date_sub( curdate(), INTERVAL 90 DAY ) <= pay_ord_date
AND order_state_type IN(${states})
and seller_id = #{sellerId,jdbcType=BIGINT}
and refer_ord_date >= #{startDate,jdbcType=BIGINT}
and refer_ord_date < #{endDate,jdbcType=BIGINT}
GROUP BY order_state_type

SELECT t_.md_ xPoint, IFNULL(ot_.count_, 0) yValue FROM ( SELECT DATE_FORMAT(DATE_SUB(SYSDATE(), INTERVAL st.id DAY), '%m-%d') md_ FROM statistics_temp st WHERE st.id < 30 ORDER BY st.id DESC ) t_ LEFT JOIN ( SELECT DATE_FORMAT(oi.create_date, '%m-%d') md_, COUNT(DATE_FORMAT(oi.create_date, '%m-%d')) count_ FROM order_info oi WHERE oi.create_date >= DATE_SUB(SYSDATE(), INTERVAL 29 DAY)
AND oi.seller_id = #{sellerId}
GROUP BY DATE_FORMAT(oi.create_date, '%m-%d') ) ot_ ON t_.md_ = ot_.md_ ORDER BY t_.md_

SELECT t_.md_ xPoint, IFNULL(ot_.totalAmount, 0) yValue FROM ( SELECT DATE_FORMAT(DATE_SUB(SYSDATE(), INTERVAL st.id DAY), '%m-%d') md_ FROM statistics_temp st WHERE st.id < 30 ORDER BY st.id DESC ) t_ LEFT JOIN ( SELECT DATE_FORMAT(oi.create_date, '%m-%d') md_, ROUND(SUM(oi.amount_payable), 2) totalAmount FROM order_info oi WHERE oi.create_date >= DATE_SUB(SYSDATE(), INTERVAL 29 DAY)
AND oi.seller_id = #{sellerId}
GROUP BY DATE_FORMAT(oi.create_date, '%m-%d') ) ot_ ON t_.md_ = ot_.md_ ORDER BY t_.md_
